mapper.update:
update fy_notice
set notice_content = #{noticeContent},
update_time = #{updateTime}
where notice_id = #{noticeId}

update fy_notice

    set is_used = 0


    set is_used = #{state}


    where notice_id != #{noticeId}


    where notice_id = #{noticeId}

mapper.select:
select * from fy_notice
order by is_used desc, notice_id desc

    , ${orderColumn} ${orderType}
