mapper.update:
update fy_user


        tel = #{tel}


        password = #{newPassword}


where user_id = #{userId}
and user_type = 0

mapper.select:
select * from fy_user
where user_id = #{userId}
and password = #{password}
and user_type = 0

select count(*) from fy_order
where sell_user_id = #{userId}
and state = 1

select count(*) from fy_order
where buy_user_id = #{userId}
and state = 1
